(115, 1, '2025-10-16 09:11:32', '2025-10-16 09:11:32', '        <div class=\"breadcrumbs\">\r\n            <div>\r\n                <!-- Updated breadcrumb title -->\r\n                <h3>Our Work</h3>\r\n                <a href=\"index.html\">Home</a> / <span>Our Work</span>\r\n            </div>\r\n        </div>\r\n\r\n        <!-- Work Header -->\r\n        <section class=\"work-header\">\r\n            <!-- Updated header content -->\r\n            <h1>Our Research & Impact Areas</h1>\r\n            <p>We focus on critical intersections of technology, law, and development, providing evidence-based research\r\n                and policy engagement across several key themes that shape the future of Africa\'s digital economy.</p>\r\n        </section>\r\n\r\n        <!-- Work Themes Section -->\r\n        <section class=\"work-themes-section\">\r\n            <h2>Core Research Pillars</h2>\r\n            <div class=\"themes-grid\">\r\n                <!-- Work Theme Card 1: Artificial Intelligence -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-brain\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>Artificial Intelligence</h3>\r\n                        <p class=\"theme-focus\">Policy Research & Ethics</p>\r\n                        <p>Analyzing the legal and ethical implications of AI adoption in Kenya and Africa, focusing on\r\n                            fairness, accountability, and transparency in deployment across various sectors.</p>\r\n                    </div>\r\n                </div>\r\n                <!-- Work Theme Card 2: Data Governance & Policy -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-database\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>Data Governance & Policy</h3>\r\n                        <p class=\"theme-focus\">Regulatory Frameworks</p>\r\n                        <p>Investigating local and cross-border data flows, developing policy recommendations for data\r\n                            protection, digital sovereignty, and privacy rights in the digital age.</p>\r\n                    </div>\r\n                </div>\r\n                <!-- Work Theme Card 3: Digital Identity -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-fingerprint\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>Digital Identity</h3>\r\n                        <p class=\"theme-focus\">Rights & Inclusion</p>\r\n                        <p>Researching the legal frameworks governing digital identity systems, their impact on\r\n                            inclusion\r\n                            and exclusion, and associated human rights concerns for vulnerable populations.</p>\r\n                    </div>\r\n                </div>\r\n                <!-- Work Theme Card 4: IP and Innovation -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-lightbulb\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>IP and Innovation</h3>\r\n                        <p class=\"theme-focus\">Commercialization Support</p>\r\n                        <p>Providing analysis on patent law, copyright, trademarks, and how to create an effective legal\r\n                            environment for technology transfer and innovation in the region.</p>\r\n                    </div>\r\n                </div>\r\n                <!-- Work Theme Card 5: Cyber Law & Policy -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-shield-alt\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>Cyber Law & Policy</h3>\r\n                        <p class=\"theme-focus\">Security & Legislation</p>\r\n                        <p>Focusing on national cybersecurity legislation, combating cybercrime, and the legal\r\n                            challenges\r\n                            posed by digital security threats, surveillance, and critical infrastructure protection.</p>\r\n                    </div>\r\n                </div>\r\n                <!-- Work Theme Card 6: Digital Trade & E-commerce -->\r\n                <div class=\"work-theme-card\">\r\n                    <div class=\"work-theme-icon\">\r\n                        <i class=\"fas fa-globe-africa\"></i>\r\n                    </div>\r\n                    <div class=\"work-theme-content\">\r\n                        <h3>Digital Trade & E-commerce</h3>\r\n                        <p class=\"theme-focus\">Market Access & Regulation</p>\r\n                        <p>Examining legal and policy issues related to digital trade agreements, consumer protection,\r\n                            and the regulatory environment for cross-border e-commerce in Africa.</p>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n        </section>', 'our-work', '', 'inherit', 'closed', 'closed', '', '95-revision-v1', '', '', '2025-10-16 09:11:32', '2025-10-16 09:11:32', '', 95, 'https://cipit.revamp.org.test/?p=115', 0, 'revision', '', 0),
